import React, {useEffect, useState, useContext} from 'react'
import { UserContext } from '../../App'
import {useParams} from 'react-router-dom'

const Profile  = ()=>{
    const [userProfile,setProfile] = useState(null)

    const {state,dispatch} = useContext(UserContext)
    const {userid} = useParams()
    console.log(userid)
    // console.log("hiii")
  useEffect(()=>{

    fetch(`/user/${userid}`,{
        headers:{
              "Authorization":"Bearer "+localStorage.getItem("jwt")
          }
        }).then(res=>res.json())
        .then(result=>{
            // console.log("hi")
            console.log(result)

            setProfile(result)
        })
     },[])

     const followUser = ()=>{
        fetch('/follow',{
            method:"put",
            headers:{
                "Content-Type":"application/json",
                "Authorization":"Bearer "+localStorage.getItem('jwt')
            },
            body:JSON.stringify({
                followId:userid
            })
        }).then(res=>res.json())
        .then(data=>{console.log(data)})
    }

   return (
       <div style={{maxWidth:"650px",margin:"0px auto"}}>
           <div style={{
               display:'flex',
               justifyContent:"space-around",
               margin:"120px 0px",
               borderBottom:"1px solid grey"
           }}>
               <div>
               <img style={{width:"160px",height:"160px",borderRadius:"80px"}} src="https://images.unsplash.com/photo-1544005313-94ddf0286df2?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=776&q=80" />
               </div>
               {userProfile ? (<div>
               <h4>{userProfile.user.name}</h4>
                   <h5>{userProfile.user.email}</h5>
                   <div style={{display:"flex",justifyContent:"space-between",width:"108%"}}>
                       <h6>{userProfile.posts.length} posts</h6>
                       <h6>{userProfile.user.followers.length} followers</h6>
                       <h6>{userProfile.user.following.length} following</h6>

                 </div>
                 <button style={{
                       margin:"10px"
                   }} className="btn waves-effect waves-light #64b5f6 blue darken-1"
                    onClick={()=>followUser()}
                    >
                        Follow
                    </button>

               </div>): null}
           </div>
           {console.log(userProfile)}
           <div className="gallery">
               {
                  userProfile ?
                   userProfile.posts.map(item=>{
                       return(
                           <img key={item._id} className='item' src={item.photo} alt={item.title} />
                       )
                   }) : null
               }
           </div>
       </div>
   )

}


export default Profile
