import { useContext,useRef,useEffect } from 'react'
import {Link,useNavigate} from 'react-router-dom'
import { UserContext } from '../App'
import M from 'materialize-css'
const Navbar = ()=>{
    const searchModal = useRef(null);
    const history = useNavigate();
    const {state,dispatch} = useContext(UserContext);
    useEffect(()=>{
        M.Modal.init(searchModal.current)
    },[])
    const renderList = ()=>{
        if(state)
        {
            return [
                <li key="1"> <i data-target="modal1" className="large material-icons modal-trigger" style={{color:"black"}}>search</i></li>,
                <li key="2"><Link to="/profile">Profile</Link></li>,
                <li key="3"><Link to="/create">Create Post</Link></li>,
                <li key="4"><Link to="/myfollowingpost">My Following Posts</Link></li>,
                <li key="5">
                     <button class="btn #e53935 red darken-1" onClick={onClick=>{
                         localStorage.clear()
                         dispatch({type:"CLEAR"})
                         history('/signin')
                     }}>Sign Out

  </button>
                </li>

            ]
        }
        else{
             return [
                <li key="6"><Link to="/Signin">Sign IN</Link></li>,
                <li key="7"><Link to="/Signup">Sign UP</Link></li>
             ]
        }
    }
    return (
        <nav>
        <div className="nav-wrapper white">
          <Link to={state?"/":"/signin"} className="brand-logo left">Upload</Link>
          <ul id="nav-mobile" className="right">
              {renderList()}





          </ul>
        </div>
        <div id="modal1" className="modal" ref={searchModal }>
    <div className="modal-content">
      <h4>Modal Header</h4>
      <p>A bunch of text</p>
    </div>
    <div className="modal-footer">
      <button className="modal-close waves-effect waves-green btn-flat">Agree</button>
    </div>
  </div>
      </nav>
    )
}

export default Navbar
